mapper.insert:

INSERT INTO myinfo (user_id, id, name, address, password, joindate, email, number)
SELECT IFNULL(MAX(user_id), 0) + 1, #{id}, #{name}, #{address}, #{password}, sysdate(), #{email}, #{number}
FROM myinfo

mapper.select:

SELECT user_id, id, name, zip_code, address, address_detail, password, joindate, email, number
FROM myinfo 
WHERE user_id = #{user_id}

mapper.update:

UPDATE 	myinfo
SET		password=#{password}, name=#{name}, zip_code=#{zip_code}, email=#{email}, address=#{address}, address_detail=#{address_detail}, number=#{number}
WHERE	user_id = #{user_id}

mapper.select:

SELECT count(*)
FROM myinfo
WHERE id = #{id}
