mapper.insert:

INSERT INTO review (review_no, user_id, product_no, subject, content, reg_date, rating, img_url)
SELECT IFNULL(MAX(review_no), 0) + 1, #{user_id}, #{product_no}, #{subject}, #{content}, now(), #{rating}, #{img_url}
FROM review

mapper.update:

UPDATE	review
SET		SUBJECT = #{subject}, CONTENT = #{content}, RATING = #{rating}
WHERE	review_no = #{review_no}
